import React, { useEffect, useState, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { connect } from "./redux/blockchain/blockchainActions";
import { fetchData } from "./redux/data/dataActions";
import * as s from "./styles/globalStyles";
import styled from "styled-components";
import i1 from "./assets/images/mintkeeper.jpg";
import img1 from "./assets/images/key1.png";
import img2 from "./assets/images/key2.png";
import img3 from "./assets/images/key3.png";
import img4 from "./assets/images/key4.png";
import img5 from "./assets/images/key5.png";
import newbanner from "./assets/images/newbane.png"

export const StyledButton = styled.button`
  padding: 10px;
  border-radius: 50px;
  border: solid;
  background-color: black;
  padding: 10px;
  fontFamily: Staatliches, cursive;
  padding-left: 15px;
  padding-right: 15px;
  font-weight: bold;
  font-size: 40px;
  color: white;
  cursor: pointer;
  box-shadow: 0px 6px 0px -2px black;
  -webkit-box-shadow: 0px 6px 0px -2px black;
  -moz-box-shadow: 0px 6px 0px -2px black;
  :active {
    box-shadow: none;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    color: #white;
  }
  :hover {
    transition-duration: .4s;
    color: #94E8E8;
  }
`;

export const ResponsiveWrapper = styled.div`
  display: flex;
  flex: 1;
  flex-direction: column;
  justify-content: stretched;
  align-items: stretched;
  width: 100%;
  @media (min-width: 767px) {
    flex-direction: row;
  }
`;

export const StyledImg = styled.img`
  width: 200px;
  height: 200px;
  @media (min-width: 767px) {
    width: 500px;
    height: 250px;
  }
  transition: width 0.5s;
  transition: height 0.5s;
`;

function App() {
  const dispatch = useDispatch();
  const blockchain = useSelector((state) => state.blockchain);
  const data = useSelector((state) => state.data);
  const [feedback, setFeedback] = useState("");
  const [claimingNft, setClaimingNft] = useState(false);
  const claimNFTs = (_amount) => {
    _amount = document.getElementById("inputBox").value;
    if (_amount <= 0) {
      return;
    }
    setFeedback("Minting your Official Cryptic Key(s)...");
    setClaimingNft(true);
    blockchain.smartContract.methods
      .mint(blockchain.account, _amount)
      // ********
      // You can change the line above to
      // .whiteListMint(blockchain.account, _amount) if you want only whitelisted
      // users to be able to mint through your website!
      // And after you're done with whitelisted users buying from your website,
      // You can switch it back to .mint(blockchain.account, _amount).
      // ********
      .send({
        gasLimit: 285000 * _amount,
        to: "0xCF443a9DA074E8071e82caed6f9DF2A489E303ad",
        from: blockchain.account,
        value: blockchain.web3.utils.toWei((0.05 * _amount).toString(), "ether"),
      })
      .once("error", (err) => {
        console.log(err);
        setFeedback("Sorry, something went wrong. Check your transaction on Etherscan to find out what happened!");
        setClaimingNft(false);
      })
      .then((receipt) => {
        setFeedback(
          "CONGRATS! Your OJINGEO successfully minted! Good luck, you're officially in the game. "
        );
        setClaimingNft(false);
        dispatch(fetchData(blockchain.account));
      });
  };

  const getData = () => {
    if (blockchain.account !== "" && blockchain.smartContract !== null) {
      dispatch(fetchData(blockchain.account));
    }
  };

  useEffect(() => {
    getData();
  }, [blockchain.account]);

  return (
    <s.Screen style={{ backgroundColor: "var(--black)", fontSize: 40 }}>
      <s.Container flex={1} ai={"center"} style={{ padding: 0, backgroundColor: "pink", flexDirection: "column"}}>
        {/* <s.TextTitle
          style={{ 
          textAlign: "left", 
          fontSize: 40, 
          fontWeight: "bold", 
          paddingRight: 10, 
          margin: 0, 
          
          borderStyle: "solid", 
          borderColor: "white", 
          borderWidth: 0,
          paddingLeft: 30,
          paddingRight: 30,
          paddingTop: 30,
          paddingBottom: 10,
          
          borderRadius: 20, 
          textAlign: "left", 
          justifySelf: "left", 
          justifyContent: "left", 
          color:"#7C3AED", 
          background: "black",
          }}
        > */}
          {/* Squid Game */}
        {/* </s.TextTitle> */}
        <div style={{
          display:"flex",
          textAlign: "left", 
          justifySelf: "left", 
          justifyContent: "left",
          }}>
        <a 
        style={{
          textAlign: "left", 
          justifySelf: "left", 
          justifyContent: "left",
          paddingLeft: 0,
        }}
        
        href="https://niknamesnft.com"><StyledImg alt={"NikNames"} 
          src={newbanner} 
          style={{textAlign: "center",
          }}></StyledImg></a>

        </div>
        <ResponsiveWrapper flex={1} style={{ padding: 24, paddingTop: 0 }}>
          <s.Container flex={1} jc={"center"} ai={"center"} style={{paddingTop: 0}}>
          <s.TextTitle
              style={{ 
              color: "white",
              textAlign: "center", 
              maxWidth: 1000,
              fontSize: 30,
              paddingBottom: 0, 
              fontWeight: "bold", 
              borderStyle: "solid", 
              borderColor: "black",}}>
                  A <text style={{color: "#94E8E8"}}>Cryptic Key</text> is an NFT that is high in utility and extremity rare.
                  These keys open arks which are digital treasure chests that have
                  physical and digital prizes. They also act as tickets to our
