import React, { useEffect, useState, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { connect } from "./redux/blockchain/blockchainActions";
import { fetchData } from "./redux/data/dataActions";
import * as s from "./styles/globalStyles";
import styled from "styled-components";
import i1 from "./assets/images/mintkeeper.jpg";
import img1 from "./assets/images/key1.png";
import img2 from "./assets/images/key2.png";
import img3 from "./assets/images/key3.png";
import img4 from "./assets/images/key4.png";
import img5 from "./assets/images/key5.png";
import img6 from "./assets/images/key6.png";
import newbanner from "./assets/images/mintingbanner3.jpeg"

export const StyledButton = styled.button`
  border-radius: 50px;
  border: solid;
  background-color: black;
  padding: 10px;
  text-align: center;

  fontFamily: Roboto, sans-serif;
  font-weight: bold;
  font-size: 40px;
  color: white;
  cursor: pointer;
  box-shadow: 0px 6px 0px -2px black;
  -webkit-box-shadow: 0px 6px 0px -2px black;
  -moz-box-shadow: 0px 6px 0px -2px black;
  :active {
    box-shadow: none;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    color: #white;
  }
  :hover {
    transition-duration: .4s;
    color: #94E8E8;
  }
  @media (min-width: 767px) {
    padding-left: 40px;
  }
`;

export const ResponsiveWrapper = styled.div`
  display: flex;
  flex: 1;
  flex-direction: column;
  justify-content: stretched;
  align-items: stretched;
  width: 100%;
  @media (min-width: 767px) {
    flex-direction: row;
  }
`;

export const StyledImg = styled.img`
  width: 403px;
  height: 176.5px;
  box-shadow: 0px 0px 0px #88429B;
  @media (min-width: 767px) {
    width: 806px;
    height: 337px;
    box-shadow: 0px 0px 50px #020004;
  }
  transition: width 0.5s;
  transition: height 0.5s;
`;

function App() {
  const dispatch = useDispatch();
  const blockchain = useSelector((state) => state.blockchain);
  const data = useSelector((state) => state.data);
  const [feedback, setFeedback] = useState("");
  const [claimingNft, setClaimingNft] = useState(false);
  const claimNFTs = (_amount) => {
    _amount = document.getElementById("inputBox").value;
    if (_amount <= 0) {
      return;
    }
    setFeedback("Minting your Official Cryptic Key(s)...");
    setClaimingNft(true);
    blockchain.smartContract.methods
      .mint(blockchain.account, _amount)
      // ********
      // You can change the line above to
      // .whiteListMint(blockchain.account, _amount) if you want only whitelisted
      // users to be able to mint through your website!
      // And after you're done with whitelisted users buying from your website,
      // You can switch it back to .mint(blockchain.account, _amount).
      // ********
      .send({
        gasLimit: 285000 * _amount,
        to: "0xaa5598872fc369e81a4c87505706f3d1d3d9d458",
        from: blockchain.account,
        value: blockchain.web3.utils.toWei((0.05 * _amount).toString(), "ether"),
      })
      .once("error", (err) => {
        console.log(err);
        setFeedback("Sorry, something went wrong. Check your transaction on Etherscan to find out what happened!");
        setClaimingNft(false);
      })
      .then((receipt) => {
        setFeedback(
          "CONGRATS! Your Cryptic Key successfully minted!"
        );
        setClaimingNft(false);
        dispatch(fetchData(blockchain.account));
      });
  };

  const getData = () => {
    if (blockchain.account !== "" && blockchain.smartContract !== null) {
      dispatch(fetchData(blockchain.account));
    }
  };

  useEffect(() => {
    getData();
  }, [blockchain.account]);

  return (
    <s.Screen style={{ backgroundColor: "var(--black)", fontSize: 40 }}>
      <s.Container flex={1} ai={"center"} style={{ padding: 0, backgroundColor: "#C5F3AB", flexDirection: "column"}}>
        <div style={{
          display:"flex",
          textAlign: "left", 
          justifySelf: "left", 
          justifyContent: "left",
          }}>
        <s.TextTitle style={{}}>
        <a 
        style={{
          textAlign: "left", 
          justifySelf: "left", 
          justifyContent: "left",
          paddingLeft: 30,
          paddingTop: 30,
        }}
        
        href="https://opensea.io/collection/cryptickeys"><StyledImg alt={"NikNames"} 
          src={newbanner} 
          style={{textAlign: "center",paddingTop: 30, 
          }}></StyledImg></a>
          </s.TextTitle>
          
        </div>
        <div style={{}}>
          <span style={{}}>
          <s.TextTitle
              style={{ 
              color: "white",
              textAlign: "center", 
              maxWidth: 500,
              fontSize: 50,
              marginTop: 100,
              paddingBottom: 0,
